pragma solidity ^0.4.24;

import "./ResearchSpecificToken.sol";

contract ResearchSpecificToken_v2 is ResearchSpecificToken{

	uint public version;

	constructor(
        uint8 _decimalUnits,
        string _tokenName,
        string _tokenSymbol,
        uint _version
    ) ResearchSpecificToken(_decimalUnits,_tokenName, _tokenSymbol) public{
        version = _version;
    }
}
